function WordForm(props) {
  // render
  const renderInputs = (event) => {
    console.log(event.target.value);
    return null // implement changes
  }

  return (
    <div id="div-words">
      { renderInputs() }
      <form onChange={renderInputs}>
      {props.words.map((word, index)=>
      <div>
      <input type="text" key={index}></input>
      <p>{index} {word.key} {word.label}</p>
      </div>
      )}
      </form>
      {console.log(props.words)}
    </div>
  )
}

export default WordForm
