    return null // implement changes
  }

  return (
    <div id="div-words">
      {/* <form onChange={renderInputs}> */}
      {props.words.map((word, index)=>
      <div>
      <input id={index} type="text" key={index} onChange={renderInput}></input>
      <p>{index} {word.key} {word.label}</p>
      </div>
      )}
      {/* </form> */}
      {console.log(props.words)}
    </div>
  )
}

